import { useDispatch } from 'react-redux';
import React, { useState } from 'react';
import {Link} from 'react-router-dom';
import {setGameType} from '../actions/gametype';

const aiScoreFromLocalStorage = JSON.parse(localStorage.getItem("aiScore") || "0")
const playerScoreFromLocalStorage = JSON.parse(localStorage.getItem("playerScore") || "0")

function Landing(){
    const [aiScore, IncrementAIScore] = useState(aiScoreFromLocalStorage);
    const [playerScore, incrementScore] = useState(playerScoreFromLocalStorage);
    const dispatch = useDispatch();

    function setNormalGame(){
        let gametype = "normal";
        dispatch(setGameType(gametype));
        if (aiScore >= 17 || playerScore >= 17) {
            incrementScore(0);  
            IncrementAIScore(0);
        }
    }

    function setFreePlay(){
        let gametype = "free";
        dispatch(setGameType(gametype));
    }
    return(
        <div class="container">
        <h1>Welcome to Battleship</h1>
        <p>Please choose your game type</p>
        <div class="game_mode">
            <Link to="/game-board" gameType="normal"><button class="buttons game" onClick={setNormalGame}>Normal Game</button></Link>
            <Link to="/game-board" gameType="freePlay"><button class="buttons game" onClick={setFreePlay}>Free Play</button></Link>
        </div>
        <Link to="/instructions"><button class="buttons bottom_button">Instructions >>> </button></Link>
    </div>
    )
} 

export default Landing;
